import React from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";

import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import TestContentPanel from "./testContentPanel";

const useStyles = makeStyles((theme) => ({
  fixHeightToScreenSize: {
    width: "100%",
    height: "100%",

  },
}));

const ALayout = (props) => {
  const classes = useStyles();

  let gridContainerProps = {};
  if (props.fixHeader === undefined || props.fixHeader === true) {
    gridContainerProps.className = classes.fixHeightToScreenSize;
  }
  return (
    <Grid container spacing={0} height="100%">
      <Grid item xs={12}>
        <Paper>header</Paper>
      </Grid>
      <Grid item xs={3}>
        <Paper>left panel</Paper>
      </Grid>
      <Grid item xs={3}>
        <Paper>
          <TestContentPanel />
        </Paper>
      </Grid>
      <Grid item xs={12}>
        <Paper>footer</Paper>
      </Grid>
    </Grid>
  );
};
export default ALayout;
