import React from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";

import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import TestContentPanel from "./testContentPanel";

const useStyles = makeStyles((theme) => ({
  paper: {
    background: "ffffff",
  },
  stickyHeader: {
    position: "sticky",
    top: "0px",
  },
  stickyFooter: {
    position: "sticky",
    bottom: "0px",
  },
}));

/*
Classic Layout
|---------------------------|
|           Header          |
|---------------------------|
|      |                    |
| Side |      Content       |
| Panel|       Panel        |
|      |                    |
|      |                    |
|-------------------------|
|          Footer         |
|-------------------------|

*/

const ALayout = (props) => {
  const classes = useStyles();

  let gridContainerProps = {};
  let headerProps = {};
  let navbarProps = {};
  let sidePanelProps = {};
  let contentPanelProps = {};
  let footerProps = {};

  if (props.stickyHeader === undefined || props.stickyHeader === true)
    headerProps.className = classes.stickyHeader;
  if (props.stickyFooter === undefined || props.stickyFooter === true)
    footerProps.className = classes.stickyFooter;

  return (
    <Grid container {...gridContainerProps}>
      <Grid item xs={12} {...headerProps}>
        <Paper classes={classes.paper}>header</Paper>
      </Grid>
      <Grid item xs={3} {...sidePanelProps}>
        <Paper>Side panel</Paper>
      </Grid>
      <Grid item xs={9} {...contentPanelProps}>
        <Paper>
          <TestContentPanel />
        </Paper>
      </Grid>
      <Grid item xs={12} {...footerProps}>
        <Paper>footer</Paper>
      </Grid>
    </Grid>
  );
};
export default ALayout;
