import "./App.css";
import Box from "@material-ui/core/Box";
import AForm from "./components/aForm";
import ASection from "./components/aSection";
import ATextField from "./components/aTextField";
import Button from "@material-ui/core/Button";
import { tSExternalModuleReference } from "@babel/types";

function App() {
  const [mode, setMode] = useState("edit");
  const [variant, setVariant] = useState("classic");
  return (
    <div className="App">
      <Box width="800px">
        <ASection title="Test Form" collapsable={false}>
          <AForm defaultMode="edit" defaultVariant="classic">
            <ATextField
              name="id"
              label="ID"
              defaultValue="123"
              mode="readOnly"
            />
            <ATextField
              name="hiddenField"
              defaultValue="secret"
              mode="hidden"
            />
            <ATextField
              name="firstName"
              defaultValue="abc"
              required
              info="This is some information for you!"
            />
            <ATextField
              name="lastName"
              defaultValue="def"
              required
              error
              helperText="Text to help you with!"
            />
            <ATextField
              name="address1"
              label="Address"
              autocompleteOptions={[
                { label: "Label 1", value: "Value 1" },
                { label: "Label 2", value: "Value 2" },
              ]}
              autocompleteGetOptionLabel={(option) => option.value}
            />
            <ATextField
              label="Star Wars API"
              autocompleteService="test"
              autocompleteGetOptionLabel={(option) => option.name}
            />
            <ATextField label="Comments" multiline rows="4" />
            <ATextField
              name="question1"
              label="Q1. Whats is your name? Please enter your full name and not your nickname. Please share your life story and all your achievements in life."
              multiline
              rows="4"
              variant="questionnaire"
              info="test test test test info!!!!"
            />

            <Button type="submit">Submit</Button>
            <Button
              onClick={() => {
                let newMode;
                if (mode === "edit") newMode = "disabled";
                else if (mode === "disabled") newMode = "readOnly";
                else if (mode === "readOnly") newMode = "hidden";
                setMode(newMode);
              }}
            >
              ToggleMode
            </Button>
            <Button
              onClick={() => {
                let newVariant;
                if (variant === "classic") newMode = "questionnaire";
                else if (mode === "questionnaire") newMode = "standard";
                else if (mode === "standard") newMode = "filled";
                else if (mode === "outlined") newMode = "filled";
                setVariant(newVariant);
              }}
            >
              ToggleVariant
            </Button>
          </AForm>
        </ASection>
      </Box>
    </div>
  );
}

export default App;
