import React from "react";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Button from "@material-ui/core/Button";
import Menu from "@material-ui/core/Menu";
import Box from "@material-ui/core/Box";

const useStyles = makeStyles((theme) => ({
  checkbox: {
    marginLeft: "10px",
  },
}));

const AFastTableFilterOptionsMenu = (props) => {
  const classes = useStyles()

  const menuProps = {
    getContentAnchorEl: null,
    anchorOrigin: {
      vertical: "bottom",
      horizontal: "left",
    },
    transformOrigin: { vertical: "top", horizontal: "left" },
    PaperProps: {
      style: {
        width: "250px",
        padding: "20px",
      },
    },
  };
  if (props.onClose !== undefined) menuProps.onClose = props.onClose;
  if (props.open !== undefined) menuProps.open = props.open;
  if (props.anchorEl !== undefined) menuProps.anchorEl = props.anchorEl;

  return (
    <Menu id={"_filter_menu_" + props.field} {...menuProps}>
      <FormGroup>
        {props.optionValues.map((optionValue, index) => {
          return (
            <FormControlLabel
              key={"_filter_options_" + props.field + "_" + index}
              control={
                <Checkbox
                  defaultChecked
                  name={"_filter_options_" + props.field}
                  value={optionValue}
                  color="primary"
                />
              }
              label={optionValue}
            />
          );
        })}
      </FormGroup>
      <Box style={{ width: "100%", height: "30px" }} />
      <Box style={{ display: "flex", justifyContent: "space-between" }}>
        <Button
          variant="outlined"
          color="primary"
          onClick={() => {
            props.onClose();
          }}
        >
          Cancel
        </Button>
        <Button
          variant="contained"
          color="primary"
          onClick={() => {
            //props.onApply();
            props.onClose();
          }}
        >
          Apply
        </Button>
      </Box>
    </Menu>
  );
};

export default AFastTableFilterOptionsMenu;
