import React, { useState, useEffect } from "react";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import AFastTable from "./aFastTable";
import ATextField from "./aTextField";
import ASearchPaginationBar from "./aSearchPaginationBar";

const ASearch = (props) => {
  const { columnDefs } = props;
  const [searchState, setSearchState] = useState({
    rowData: fetchRowData(),
    pageNum: 1,
  });



  const handleGoToPage = (pageNum) => {
    setSearchState({ rowData: searchState.rowData, pageNum: pageNum });
  };

  return (
    <React.Fragment>
      <Box style={{ margin: "20px" }}>
        <ATextField name="search" />{" "}
        <Button variant="contained" color="primary">
          Search
        </Button>
        <Box style={{ height: "20px" }} />
        <AFastTable
          columnDefs={columnDefs}
          rowData={searchState.rowData}
          rowSelection="multiple"
        />
      </Box>
      <ASearchPaginationBar
        totalPages={13}
        pageNum={6}
        maxPages={9}
        onGoToPage={handleGoToPage}
      />
    </React.Fragment>
  );
};

export default ASearch;
