import React from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";

import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import TestContentPanel from "./testContentPanel";

const useStyles = makeStyles((theme) => ({
  fixHeightToScreenSize: {
    width: "100%",
    height: "100%",
  },
  overflowScroll: {
    overflow: "scroll",
  },
}));

const ALayout = (props) => {
  const classes = useStyles();

  let gridContainerProps = {};
  let headerProps = {};
  let navbarProps = {};
  let sidePanelProps = {};
  let contentPanelProps = {};
  let footerProps = {};

  if (props.fixHeader === undefined || props.fixHeader === true) {
    gridContainerProps.className = classes.fixHeightToScreenSize;
    contentPanelProps.className = classes.overflowScroll;
  }
  return (
    <Grid container {...gridContainerProps}>
      <Grid item xs={12} {...headerProps}>
        header
      </Grid>
      <Grid item xs={12} {...navbarProps}>
        navbar
      </Grid>
      <Grid item xs={3} {...contentPanelProps}>
        <Paper>left panel</Paper>
      </Grid>
      <Grid item xs={9} {...contentPanelProps}>
        <Paper>
          <TestContentPanel />
        </Paper>
      </Grid>
      <Grid item xs={12} {...footerProps}>
        <Paper>footer</Paper>
      </Grid>
    </Grid>
  );
};
export default ALayout;
