import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import NavigateBeforeIcon from "@material-ui/icons/NavigateBefore";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";
import FirstPageIcon from "@material-ui/icons/FirstPage";
import LastPageIcon from "@material-ui/icons/LastPage";

const useStyles = makeStyles((theme) => ({
  paginationBar: {
    margin: "10px 0 10px 0",
  },
  pageButtons: {
    backgroundColor: theme.palette.secondary.contrastText,
    minWidth: "30px",
    maxWidth: "30px",
    minHeight: "30px",
    maxHeight: "30px",
    margin: "0px 5px 0px 5px",
  },
  currentPageButton: {
    minWidth: "30px",
    maxWidth: "30px",
    minHeight: "30px",
    maxHeight: "30px",
    margin: "0px 5px 0px 5px",
  },
}));

const ASearchPaginationBar = (props) => {
  const { totalPages, pageNum, onGoToPage, maxPages } = props;

  const classes = useStyles();

  let numPagesToShow = 9; //default to 9 if prop not provided
  if (maxPages !== undefined) numPagesToShow = maxPages;

  let startPageNum = pageNum - Math.floor(numPagesToShow / 2);
  let endPageNum = pageNum + Math.floor(numPagesToShow / 2);

  if (startPageNum < 1) {
    endPageNum = numPagesToShow + startPageNum;
    startPageNum = 1;
  } else if (endPageNum > totalPages) {
    console.log("here");
    startPageNum = startPageNum - (endPageNum - parseInt(totalPages));
    endPageNum = totalPages;
  }

  console.log(
    "startPageNum: " +
      startPageNum +
      " | totalPages: " +
      totalPages +
      " | pageNum: " +
      pageNum +
      " | numPagesToShow: " +
      numPagesToShow +
      " | endPageNum: " +
      endPageNum
  );

  const handleGoToPage = (event) => {
    event.stopPropagation();
    onGoToPage(event, event.currentTarget.value);
  };

  return (
    <Box className={classes.paginationBar}>
      <Button
        disabled={pageNum <= 1 ? true : false}
        className={classes.pageButtons}
        variant="outlined"
        color="primary"
        value={1}
        onClick={handleGoToPage}
      >
        <FirstPageIcon />
      </Button>
      <Button
        disabled={pageNum <= 1 ? true : false}
        className={classes.pageButtons}
        variant="outlined"
        color="primary"
        value={pageNum - 1}
        onClick={handleGoToPage}
      >
        <NavigateBeforeIcon />
      </Button>
      {(() => {
        let buttons = [];
        for (let i = startPageNum; i <= endPageNum; i++) {
          let buttonProps = {};
          if (parseInt(pageNum) === i) {
            buttonProps.className = classes.currentPageButton;
            buttonProps.variant = "contained";
          } else {
            buttonProps.className = classes.pageButtons;
            buttonProps.variant = "outlined";
          }
          buttons.push(
            <Button
              key={"_page_button_" + i}
              color="primary"
              {...buttonProps}
              value={i}
              onClick={handleGoToPage}
            >
              {i}
            </Button>
          );
        }
        return buttons;
      })()}
      <Button
        disabled={pageNum >= totalPages ? true : false}
        className={classes.pageButtons}
        variant="outlined"
        color="primary"
        value={pageNum + 1}
        onClick={handleGoToPage}
      >
        <NavigateNextIcon />
      </Button>
      <Button
        disabled={pageNum >= totalPages ? true : false}
        className={classes.pageButtons}
        variant="outlined"
        color="primary"
        value={totalPages}
        onClick={handleGoToPage}
      >
        <LastPageIcon />
      </Button>
    </Box>
  );
};
export default ASearchPaginationBar;
