import React from "react";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Button from "@material-ui/core/Button";
import Menu from "@material-ui/core/Menu";
import Paper from "@material-ui/core/Paper";

const AFastTableFilterOptionsMenu = (props) => {
  const menuProps = {
    getContentAnchorEl: null,
    anchorOrigin: {
      vertical: "bottom",
      horizontal: "left",
    },
    transformOrigin: { vertical: "top", horizontal: "left" },
    PaperProps: {
      style: {
        width: "250px",
        padding: "30px 30px ",
      },
    },
  };
  if (props.onClose !== undefined) menuProps.onClose = props.onClose;
  if (props.open !== undefined) menuProps.open = props.open;
  if (props.anchorEl !== undefined) menuProps.anchorEl = props.anchorEl;

  return (
    <Menu id={"_filter_menu_" + props.field} {...menuProps}>
      <FormGroup>
        {props.optionValues.map((optionValue, index) => {
          return (
            <FormControlLabel
              key={"_filter_options_" + props.field + "_" + index}
              control={
                <Checkbox
                  defaultChecked
                  name={"_filter_options_" + props.field}
                  value={optionValue}
                  color="primary"
                />
              }
              label={optionValue}
            />
          );
        })}
      </FormGroup>
      <Button
        variant="outlined"
        color="primary"
        onClick={() => {
          props.onClose();
        }}
      >
        Cancel
      </Button>
      <Button
        variant="contained"
        color="primary"
        onClick={() => {
          //props.onApply();
          props.onClose();
        }}
      >
        Apply
      </Button>
    </Menu>
  );
};

export default AFastTableFilterOptionsMenu;
