import React from "react";
import { withStyles } from "@material-ui/core/styles";
import MuiAccordion from "@material-ui/core/Accordion";
import MuiAccordionSummary from "@material-ui/core/AccordionSummary";
import MuiAccordionDetails from "@material-ui/core/AccordionDetails";
import Typography from "@material-ui/core/Typography";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";

const useStyles = makeStyles(theme)

const Accordion = withStyles({
  root: {
    border: "1px solid rgba(0, 180, 0, 1)",
    boxShadow: "none",
    "&:not(:last-child)": {
      borderBottom: 0,
    },
    "&:before": {
      display: "none",
    },
    "&$expanded": {
      margin: "auto",
    },
    "&:hover": {
      cursor: "pointer",
    },
  },
  expanded: {},
  MuiExpansionPanelSummary: {
    root: {
      "&:hover:not(.Mui-disabled)": {
        cursor: "pointer",
      },
    },
  },
})(MuiAccordion);

const AccordionSummary = withStyles({
  root: {
    color: "#ffffff",
    backgroundColor: "rgba(00, 180, 00, 1)",
    borderBottom: "1px solid rgba(0, 0, 0, .125)",
    marginBottom: -1,
    minHeight: 50,
    "&$expanded": {
      minHeight: 50,
    },
    "&:hover": {
      cursor: "pointer",
    },
  },
  content: {
    "&$expanded": {
      margin: "12px 0",
    },
  },
  expanded: {},
})(MuiAccordionSummary);

const AccordionDetails = withStyles((theme) => ({
  root: {
    padding: theme.spacing(5),
  },
}))(MuiAccordionDetails);

const ASection = (props) => {
  let accordionProps = { defaultExpanded: true };
  let accordionSummaryProps = {};
  if (props.collapsable === undefined || props.collapsable) {
    accordionSummaryProps.expandIcon = <ExpandMoreIcon />;
  } else {
    accordionProps.expanded = true;
  }
  return (
    <Accordion square {...accordionProps}>
      <AccordionSummary {...accordionSummaryProps}>
        <Typography>{props.title}</Typography>
      </AccordionSummary>
      <AccordionDetails>{props.children}</AccordionDetails>
    </Accordion>
  );
};

export default ASection;
