import React, { useState } from "react";

import Box from "@material-ui/core/Box";

const AStepperFormPart = (props) => {
  const [stepNum, setStepNum] = useState();
  let tepNum;
  if (initialStepNum === undefined) {
    let initialStepNum = props.add_step(this, props.title);
    setStepNum(initialStepNum);
    console.log("step number: " + initialStepNum);
  }

  let boxProps = {};
  console.log("active step number: " + props.active_step);
  if (stepNum !== undefined && stepNum !== props.active_step) {
    boxProps.style = { display: "none" };
    console.log("boxProp: " + JSON.stringify(boxProps));
  }

  return <div {...boxProps}>{props.children}</div>;
};

export default AStepperFormPart;
