import React, { useState } from "react";

import Box from "@material-ui/core/Box";

const AStepperFormPart = (props) => {
  let initDefaultMode = "edit";
  if (props.defaultMode !== undefined) {
    initDefaultMode = props.defaultMode;
  } else if (props.default_mode !== undefined) {
    initDefaultMode = props.default_mode;
  }
  const [defaultMode, setDefaultMode] = useState(initDefaultMode);
  let initDefaultModeMapping = { review: "readOnly" };
  if (
    props.default_mode_mapping !== undefined &&
    props.defaultModeMapping !== undefined
  ) {
    defaultModeMapping = {
      ...initDefaultModeMapping,
      ...props.default_mode_mapping,
      ...props.defaultModeMapping,
    };
  } else if (props.default_mode_mapping !== undefined) {
    defaultModeMapping = {
      ...initDefaultModeMapping,
      ...props.default_mode_mapping,
    };
  } else if (props.defaultModeMapping !== undefined) {
    defaultModeMapping = { ...defaultModeMapping, ...props.defaultModeMapping };
  }
  const [defaultModeMapping, setDefaultModeMapping] = useState(
    initDefaultModeMapping
  );
  const [stepNum, setStepNum] = useState(-1);
  console.log("step number: " + stepNum);
  if (stepNum === -1) {
    setStepNum(props.add_step(props.name, props.title, props.optional));
    console.log("step number: " + stepNum);
  }

  let boxProps = {};
  console.log("active step number: " + props.active_step);
  if (
    stepNum !== undefined &&
    stepNum !== props.active_step &&
    props.active_step !== props.num_steps - 1 &&
    props.review_before_submit === true
  ) {
    boxProps.style = { display: "none" };
    console.log("boxProp: " + JSON.stringify(boxProps));
  }

  const childrenWithAdditionalProps = React.Children.toArray(props.children)
    .filter((n) => n)
    .map((child) => {
      let additionalProps = {};
      if (props.default_mode !== undefined)
        additionalProps.default_mode = defaultMode;
      if (props.default_mode_mapping !== undefined)
        additionalProps.default_mode_mapping = defaultModeMapping;

      console.log("aStepperFormPart:" + JSON.stringify(additionalProps));

      return React.cloneElement(child, additionalProps);
    });
  return <Box {...boxProps}>{childrenWithAdditionalProps}</Box>;
};

export default AStepperFormPart;
