import { useLayoutEffect } from "react";

import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import TestContentPanel from "./testContentPanel";

const useStyles = makeStyles((theme) => ({
  scrollable: {},
}));

const ALayout = (props) => {
  const classes = useStyles();

  let mainContainerProps = {}
  if (props.fixHeaderFooter == undefined)
  return (
    <Grid className={classes.scrollable} container spacing={0} height="100%">
      <Grid item xs={12}>
        <Paper>header</Paper>
      </Grid>
      <Grid item xs={3}>
        <Paper>left panel</Paper>
      </Grid>
      <Grid item xs={3}>
        <Paper>
          <TestContentPanel />
        </Paper>
      </Grid>
      <Grid item xs={12}>
        <Paper>footer</Paper>
      </Grid>
    </Grid>
  );
};
export default ALayout;
