import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Hidden, Typography } from "@material-ui/core";
import { BlockRounded } from "@material-ui/icons";

const useStyles = makeStyles((theme) => ({
  table: {
    width: "100%",
    //border: "thin solid #dddddd",
    borderRadius: "6px",
    borderSpacing: 0,
    overflow: "hidden",
    boxShadow: "0 2px 6px 0 rgba(0,0,0, 0.15)",
  },

  thead: {
    "& tr th, tr td": {
      backgroundColor: "#2255aa",
      color: "#ffffff",
      textAlign: "left",
      backgroundImage:
        "linear-gradient(to bottom, rgba(0, 0, 0, 0), rgba(0,0,0, 0.15) 30%, rgba(0,0,0, 0.15) 30%, rgba(0,0,0, 0.08) 100%)",
      border: 0,
      padding: "5px 15px",
    },
    "& tr:first-child td:first-child, tr:first-child th:first-child": {
      borderRadius: "6px 0 0 0",
      overflow: "hidden",
    },
    "& tr:first-child td:last-child, tr:first-child th:last-child": {
      borderRadius: "0 6px 0 0",
      overflow: "hidden",
    },
  },

  tbody: {
    "& tr td": {
      borderBottom: "thin solid #ddddddd",
      textAlign: "left",
      padding: "5px 15px",
    },
    "& tr:nth-of-type(even)": {
      backgroundColor: "#f6f6f6",
    },
    "& tr:hover": {
      backgroundColor: "#cccccc",
    },
    "& tr:hover td p .MuiTypography-root .MuiTypography-body1": {
      fontColor: "#ffffff",
    },
  },

  headerTypography: {
    fontWeight: "bold",
  },
}));

const fetchDataFromService = (service) => {
  return [
    { id: 2222, make: "Mock Data", model: "From Service", price: 22222222 },
    { id: 1231, make: "Toyota", model: "Celica", price: 35000 },
    { id: 1232, make: "Ford", model: " Mondeo", price: 32000 },
    { id: 1233, make: "Porsche", model: "Boxter", price: 35000 },
    { id: 1234, make: "Mercedes Benz", model: "S Class", price: 80000 },
  ];
};

const AFastTable = (props) => {
  const [rowData, setRowData] = useState(props.rowData);

  const classes = useStyles();

  useEffect(() => {
    if (props.service !== undefined) {
      let d = fetchDataFromService(props.service);
      setRowData(d);
    }
  }, []); //trigger only once after first render

  const renderHeaders = () => {
    const headerCells = props.columnDefs.map((columnDef, index) => {
      return (
        <th key={"_header_cell_" + index}>
          <Typography className={classes.headerTypography}>
            {columnDef.headerName}
          </Typography>
        </th>
      );
    });
    return <tr key="_header">{headerCells}</tr>;
  };

  const renderRows = () => {
    if (rowData.length === 0) {
      return <td colSpan={props.columnDef.length}>No Rows Found.</td>;
    }
    return rowData.map((row, rowIndex) => {
      const cells = props.columnDefs.map((columnDef, cellIndex) => {
        return (
          <td key={"_cell_" + cellIndex}>
            <Typography>{row[columnDef.field]}</Typography>
          </td>
        );
      });
      return <tr key={"_row_" + rowIndex}>{cells}</tr>;
    });
  };

  return (
    <table className={classes.table}>
      <thead className={classes.thead}>{renderHeaders()}</thead>
      <tbody className={classes.tbody}>{renderRows()}</tbody>
    </table>
  );
};

export default AFastTable;
