import React from "react";

import { formDataToObject } from "./utils";

function handleSubmit(event) {
  event.preventDefault();
  const formData = new FormData(event.target);
  console.log("handleSubmit: " + JSON.stringify(formDataToObject(formData)));
}

const AForm = (props) => {
  const childrenWithAdditional = React.Children.map(props.children, (child) => {
    let additionalProps = {};
    if (props.defaultMode) additionalProps.defaultMode = props.defaultMode;
    if (props.defaultVariant)
      additionalProps.defaultVariant = props.defaultVariant;

    return React.cloneElement(child, additionalProps);
  });

  return (
    <form onSubmit={handleSubmit} style={{ width: "100%" }}>
      {childrenWithDefaults}
    </form>
  );
};

export default AForm;
