import { Typography } from "@material-ui/core";
import React, { makeStyles, useEffect, useState } from "react";

const fetchDataFromService = (service) => {
  return [
    { id: 2222, make: "Mock Data", model: "From Service", price: 22222222 },
    { id: 1231, make: "Toyota", model: "Celica", price: 35000 },
    { id: 1232, make: "Ford", model: " Mondeo", price: 32000 },
    { id: 1233, make: "Porsche", model: "Boxter", price: 35000 },
    { id: 1234, make: "Mercedes Benz", model: "S Class", price: 80000 },
  ];
};

const AFastTable = (props) => {
  const [rowData, setRowData] = useState(props.rowData);

  useEffect(() => {
    if (props.service !== undefined) {
      let d = fetchDataFromService(props.service);
      setRowData(d);
    }
  }, []); //trigger only once after first render

  const renderHeaders = () => {
    const headerCells = props.columnDefs.map((columnDef, index) => {
      return (
        <th key={"_header_cell_" + index}>
          <Typography>{columnDef.headerName}</Typography>
        </th>
      );
    });
    return <tr key="_header">{headerCells.join("")}</tr>;
  };

  const renderRows = () => {
    if (rowData.length === 0) {
      return <td colSpan={props.columnDef.length}>No Rows Found.</td>;
    }
    return rowData.map((row, rowIndex) => {
      const cells = props.columnDefs.map((columnDef, cellIndex) => {
        return (
          <td key={"_cell_" + cellIndex}>
            <Typography>{row[columnDef.name]}</Typography>
          </td>
        );
      });
      return <tr key={"_row_" + rowIndex}>{cells.join("")}</tr>;
    });
  };

  return (
    <table className="classes.table">
      {renderHeaders()} {renderRows()}
    </table>
  );
};

export default AFastTable;
