import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import NavigateBeforeIcon from "@material-ui/icons/NavigateBefore";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";

const useStyles = makeStyles((theme) => ({
  paginationBar: {
    margin: "10px 0 10px 0",
  },
  pageButtons: {
    backgroundColor: theme.palette.secondary.contrastText,
    minWidth: "30px",
    maxWidth: "30px",
    minHeight: "30px",
    maxHeight: "30px",
    margin: "0px 5px 0px 5px",
  },
  currentPageButton: {
    minWidth: "30px",
    maxWidth: "30px",
    minHeight: "30px",
    maxHeight: "30px",
    margin: "0px 5px 0px 5px",
  },
}));

const ASearchPaginationBar = (props) => {
  const { totalPages, pageNum, onPageClick, maxPages } = props;
  let numPagesToShow = 9; //default to 9 if prop not provided
  if (maxPages !== undefined) numPagesToShow = maxPages;
  const startPageNum =
    totalPages > numPagesToShow && pageNum > Math.ceil(numPagesToShow / 2)
      ? pageNum - Math.ceil(numPagesToShow / 2)
      : 1;

  //const maxEndPageNum = totalPages > numPagesToShow && pageNum + Math.ceil(numPagesToShow / 2) > numPagesToShow ? pageNum + Math.ceil(numPagesToShow / 2) : totalPages;
  //  console.log("startPageNum: " + startPageNum + " | " + endPageNum );

  return (
    <Box className={classes.paginationBar}>
      <Button
        disabled={startPageNum <= 1 ? true : false}
        className={classes.pageButtons}
        variant="outlined"
        color="primary"
      >
        <NavigateBeforeIcon />
      </Button>
      {(function () {
        for (
          let i = startPageNum;
          i <= totalPages || i <= numPagesToShow;
          i++
        ) {
          <Button
            className={
              pageNum === i ? classes.currentPageButton : classes.pageButtons
            }
            variant="outlined"
            color="primary"
          >
            i
          </Button>;
        }
      })()}
      <Button
        disabled={pageNum >= totalPages ? false : true}
        className={classes.pageButtons}
        variant="outlined"
        color="primary"
      >
        <NavigateNextIcon />
      </Button>
    </Box>
  );
};
export default ASearchPaginationBar;
