import React, { useState } from "react";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import AFastTable from "./aFastTable";
import ATextField from "./aTextField";

const ASearch = (props) => {
  const { columnDefs } = props;
  const [searchState, setSearchState] = useState({ rowData: [], pageNum: 1 });

  const fetchData = () => {
    return [
      { id: 1111, make: "Mock Data", model: "From Prop", price: 1111111 },
      { id: 1231, make: "Toyota", model: "Celica", price: 35000 },
      { id: 1232, make: "Ford", model: " Mondeo", price: 32000 },
      { id: 1233, make: "Porsche", model: "Boxter", price: 35000 },
      { id: 1234, make: "Mercedes Benz", model: "S Class", price: 80000 },
    ];
  };

  useEffect(() => {
    const rowData = fetchData();
    setSearchState({ rowData: rowData, pageNum: 1 });
  }, []);

  const handleGoToPage = (pageNum) => {
    setSearchState({ rowData: searchState.rowData, pageNum: pageNum });
  };

  return (
    <React.Fragment>
      <Box style={{ margin: "20px" }}>
        <ATextField name="search" />{" "}
        <Button variant="contained" color="primary">
          Search
        </Button>
        <Box style={{ height: "20px" }} />
        <AFastTable
          columnDefs={columnDefs}
          rowData={rowData}
          rowSelection="multiple"
        />
      </Box>
      <ASearchPaginationBar
        totalPages={13}
        pageNum={6}
        maxPages={9}
        onGoToPage={handleGoToPage}
      />
    </React.Fragment>
  );
};

export default ASearch;
