import React, { useState, useEffect } from "react";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import AFastTable from "./aFastTable";
import ASearchPaginationBar from "./aSearchPaginationBar";
import { cloneChildrenWithDefaultModeProps } from "./utils";

const ASearch = (props) => {
  const { columnDefs, service, fetchDataHandler, maxPages } = props;

  const fetchData = (pageNum) => {
    //mock method...to be implemented...

    if (service !== undefined) {
        fetch("/customers") //alternate: https://swapi-deno.azurewebsites.net/api/starships
        .then((response) => response.json())
        .then((options) => setAutocompleteOptions(options));
    } else if (fetchDataHandler !== undefined) {
      //use handler from parent to fetchRowData
      fetchDataHandler;
    }
    return {
      pageNum: 1,
      totalPages: 13,
      rowData: [
        { id: 1111, make: "Mock Data", model: "From Prop", price: 1111111 },
        { id: 1231, make: "Toyota", model: "Celica", price: 35000 },
        { id: 1232, make: "Ford", model: " Mondeo", price: 32000 },
        { id: 1233, make: "Porsche", model: "Boxter", price: 35000 },
        { id: 1234, make: "Mercedes Benz", model: "S Class", price: 80000 },
      ],
    };
  };

  const data = { pageNum: 1, totalPages: 1, rowData: [] };
  const [rowData, setRowData] = useState(data.rowData);
  const [pageNum, setPageNum] = useState(data.pageNum);

  const handleGoToPage = (event, pageNum) => {
    const data = fetchData();
    setRowData(data.rowData);
    setPageNum(pageNum);
  };

  const childrenWithAdditionalProps = cloneChildrenWithDefaultModeProps(props);

  useEffect(() => {
    fetchData(1);
  }, []);

  return (
    <React.Fragment>
      <Box style={{ margin: "20px" }}>
        {childrenWithAdditionalProps}
        <Box style={{ height: "20px" }} />
        <AFastTable
          columnDefs={columnDefs}
          rowData={rowData}
          rowSelection="multiple"
        />
      </Box>
      <ASearchPaginationBar
        totalPages={data.totalPages}
        pageNum={pageNum}
        maxPages={maxPages === undefined ? 9 : maxPages}
        onGoToPage={handleGoToPage}
      />
    </React.Fragment>
  );
};

export default ASearch;
