import React from "react";

import { formDataToObject } from "./utils";

function handleSubmit(event) {
  event.preventDefault();
  const formData = new FormData(event.target);
  console.log("handleSubmit: " + JSON.stringify(formDataToObject(formData)));
}

const AForm = (props) => {
  const defsultodeMapping = {
    readOnly: "readOnly",
    edit: "edit",
    hidden: "hidden",
    disabled: "disabled",
    create: "edit",
    read: "readOnly",
    view: "readOnly",
    update: "edit",
  };

  if (props.defaultModeMappingMerge !== undefined)
    defaultModeMapping = { ...modeMapping, ...props.modeMappingMerge };

  console.log("Mode Mapping: " + JSON.stringify(modeMapping));

  const childrenWithAdditionalProps = React.Children.map(
    props.children,
    (child) => {
      let additionalProps = {default_mode_mapping: modeMapping};
      if (props.defaultMode) additionalProps.default_mode = props.defaultMode;
      if (props.defaultVariant)
        additionalProps.default_variant = props.defaultVariant;

      return React.cloneElement(child, additionalProps);
    }
  );

  return (
    <form onSubmit={handleSubmit} style={{ width: "100%" }}>
      {childrenWithAdditionalProps}
    </form>
  );
};

export default AForm;
