import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Button from "@material-ui/core/Button";
import NavigateBeforeIcon from "@material-ui/icons/NavigateBefore";
import NavigateNextIcon from "@material-ui/icons/NavigateNext";

const useStyles = makeStyles((theme) => ({
  paginationBar: {
    margin: "10px 0 10px 0",
  },
  pageButtons: {
    backgroundColor: theme.palette.secondary.contrastText,
    minWidth: "30px",
    maxWidth: "30px",
    minHeight: "30px",
    maxHeight: "30px",
    margin: "0px 5px 0px 5px",
  },
  currentPageButton: {
    minWidth: "30px",
    maxWidth: "30px",
    minHeight: "30px",
    maxHeight: "30px",
    margin: "0px 5px 0px 5px",
  },
}));

const ASearchPaginationBar = (props) => {
  const { totalPages, pageNum, onPageClick, maxPages } = props;

  const classes = useStyles();

  let numPagesToShow = 9; //default to 9 if prop not provided
  if (maxPages !== undefined) numPagesToShow = maxPages;
  const startPageNum =
    totalPages > numPagesToShow && pageNum > Math.floor(numPagesToShow / 2)
      ? pageNum - Math.floor(numPagesToShow / 2)
      : 1;

  console.log(
    "startPageNum: " +
      startPageNum +
      " | totalPages: " +
      totalPages +
      " | numPagesToShow" +
      numPagesToShow
  );

  const endPageNum =
    totalPages > numPagesToShow &&
    pageNum + Math.floor(numPagesToShow / 2) > numPagesToShow
      ? pageNum + Math.floor(numPagesToShow / 2)
      : totalPages > numPagesToShow - startPageNum ? numPagesToShow - start : totalPages;
  console.log("endPageNum: " + endPageNum);

  return (
    <Box className={classes.paginationBar}>
      <Button
        disabled={pageNum <= 1 ? true : false}
        className={classes.pageButtons}
        variant="outlined"
        color="primary"
      >
        <NavigateBeforeIcon />
        <NavigateBeforeIcon />
      </Button>
      <Button
        disabled={pageNum <= 1 ? true : false}
        className={classes.pageButtons}
        variant="outlined"
        color="primary"
      >
        <NavigateBeforeIcon />
      </Button>
      {(function () {
        let buttons = [];
        for (let i = startPageNum; i <= endPageNum; i++) {
          let buttonProps = {};
          if (pageNum === i) {
            buttonProps.className = classes.currentPageButton;
            buttonProps.variant = "contained";
          } else {
            buttonProps.className = classes.pageButtons;
            buttonProps.variant = "outlined";
          }
          buttons.push(
            <Button
              key={"_page_button_" + i}
              variant="outlined"
              color="primary"
              {...buttonProps}
            >
              {i}
            </Button>
          );
        }
        return buttons;
      })()}
      <Button
        disabled={pageNum >= totalPages ? true : false}
        className={classes.pageButtons}
        variant="outlined"
        color="primary"
      >
        <NavigateNextIcon />
      </Button>
      <Button
        disabled={pageNum >= totalPages ? true : false}
        className={classes.pageButtons}
        variant="outlined"
        color="primary"
      >
        <NavigateNextIcon />
        <NavigateNextIcon />
      </Button>
    </Box>
  );
};
export default ASearchPaginationBar;
