import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Checkbox, Radio, Typography } from "@material-ui/core";
import ArrowDownwardIcon from "@material-ui/icons/ArrowDownward";
import ArrowUpwardIcon from "@material-ui/icons/ArrowUpward";
import MenuIcon from "@material-ui/icons/Menu";
import InfoOutlined from "@material-ui/icons/InfoOutlined";

const useStyles = makeStyles((theme) => ({
  table: {
    width: "100%",
    border: "thin solid #dddddd",
    borderRadius: theme.shape.borderRadius,
    borderSpacing: 0,
    overflow: "hidden",
    boxShadow: theme.shadows[5],
  },

  thead: {
    "& tr th, tr td": {
      backgroundColor: theme.palette.secondary.main,
      textAlign: "left",
      backgroundImage:
        "linear-gradient(to bottom, rgba(0, 0, 0, 0), rgba(0,0,0, 0.15) 30%, rgba(0,0,0, 0.15) 30%, rgba(0,0,0, 0.08) 100%)",
      //border: 0,
      padding: "4px 15px",
    },
    "& tr th:not(:last-child), tr td:not(:last-child)": {
      borderRight: "thin solid rgb(77, 88, 106)",
    },
    "& tr th div, tr td div": {
      display: "flex",
      justifyContent: "space-between",
    },
  },
  headerTypography: {
    display: "inline",
    color: theme.palette.secondary.contrastText,
  },
  headerIconColor: {
    fill: theme.palette.secondary.contrastText,
  },
  headerRadio: {
    root: {
      color: theme.palette.secondary.contrastText,
      "&$checked": {
        color: theme.palette.secondary.contrastText,
      },
    },
  },
  headerCheckbox: {
    root: {
      color: theme.palette.secondary.contrastText,
      "&$checked": {
        color: theme.palette.secondary.contrastText,
      },
    },
  },

  tbody: {
    "& tr td": {
      textAlign: "left",
      padding: "4px 15px",
      color: "rgb(77, 88, 106)",
    },
    "& tr:not(:last-child) td": {
      borderBottom: "thin solid rgb(229, 233, 238)",
    },
    "& tr:hover": {
      backgroundColor: "rgb(224, 228, 236)",
      color: "#ffffff",
    },
  },

  tfoot: {
    "& tr": {
      backgroundColor: "rgb(46, 48, 73)",
      color: "#ffffff",
    },
  },
}));

const fetchDataFromService = (service) => {
  return [
    { id: 2222, make: "Mock Data", model: "From Service", price: 22222222 },
    { id: 1231, make: "Toyota", model: "Celica", price: 35000 },
    { id: 1232, make: "Ford", model: " Mondeo", price: 32000 },
    { id: 1233, make: "Porsche", model: "Boxter", price: 35000 },
    { id: 1234, make: "Mercedes Benz", model: "S Class", price: 80000 },
  ];
};

const AFastTable = (props) => {
  const [rowData, setRowData] = useState(props.rowData);

  const classes = useStyles();

  useEffect(() => {
    if (props.service !== undefined) {
      let d = fetchDataFromService(props.service);
      setRowData(d);
    }
  }, []); //trigger only once after first render

  const renderHeaders = () => {
    const headerCells = props.columnDefs.map((columnDef, index) => {
      let sortIconFragment = "";
      if (columnDef.sortable !== undefined && columnDef.sortable === true) {
        if (columnDef.sortOrder === "asc") {
          sortIconFragment = (
            <ArrowUpwardIcon className={classes.headerIconColor} />
          );
        } else if (columnDef.sortOrder === "desc") {
          sortIconFragment = (
            <ArrowDownwardIcon className={classes.headerIconColor} />
          );
        }
      }

      return (
        <th key={"_header_cell_" + index}>
          <div>
            <span>
              {columnDef.checkbox !== undefined &&
              columnDef.checkbox === true ? (
                <Radio color="default" className={classes.headerCheckbox} />
              ) : (
                ""
              )}
              {columnDef.radio !== undefined && columnDef.radio === true ? (
                <Checkbox className={classes.headerRadio} />
              ) : (
                ""
              )}
              <Typography className={classes.headerTypography}>
                {columnDef.headerName}
              </Typography>
            </span>
            <span style={{ verticalAlign: "middle", backgroundColor }}>
              {sortIconFragment}
              {columnDef.filter !== undefined && columnDef.filter === true ? (
                <MenuIcon className={classes.headerIconColor} />
              ) : (
                ""
              )}
            </span>
          </div>
        </th>
      );
    });
    return <tr key="_header">{headerCells}</tr>;
  };

  const renderFooter = () => {
    if (props.footer !== undefined) {
      return (
        <tfoot className={classes.tfoot}>
          <tr>
            <td colSpan={props.columnDefs.length}>{props.footer()}</td>
          </tr>
        </tfoot>
      );
    }
  };

  const renderRows = () => {
    if (rowData.length === 0) {
      return <td colSpan={props.columnDef.length}>No Rows Found.</td>;
    }
    return rowData.map((row, rowIndex) => {
      const cells = props.columnDefs.map((columnDef, cellIndex) => {
        return (
          <td key={"_cell_" + cellIndex}>
            <Typography>{row[columnDef.field]}</Typography>
          </td>
        );
      });
      return <tr key={"_row_" + rowIndex}>{cells}</tr>;
    });
  };

  return (
    <table className={classes.table}>
      <thead className={classes.thead}>{renderHeaders()}</thead>
      <tbody className={classes.tbody}>{renderRows()}</tbody>
      {renderFooter()}
    </table>
  );
};

export default AFastTable;
